import React, {useState} from 'react'
import '../styles/Player.css'
import OpcionesParaJugar from '../components/OpcionesParaJugar'
import IconoJugRed from '../icons/player-red.svg'

import Piedra from '../icons/piedra.svg'
import Papel from '../icons/papel.svg'
import Tijeras from '../icons/tijeras.svg'
import Lagarto from '../icons/lagarto.svg'
import Spock from '../icons/spock.svg'


function Player(consultaEleccion){

    const [eleccionJugador, setEleccionJugador] = useState('');
    const [ganadasJugador, setGanadasJugador]= useState(0);

    const pasarEleccion = eleccion => {
        consultaEleccion(eleccion);

    }



    return(

        <div>
            <div className="imagen">
                <img src={IconoJugRed} alt="imagen jugador" />
            </div>
            <div className="iconos-menu">
            <img className="iconos" src={Piedra} alt="Piedra" onClick={() => setEleccionJugador('Piedra')}/>
            <img className="iconos" src={Papel} alt="Papel" onClick={() => setEleccionJugador('Papel')}/>
            <img className="iconos" src={Tijeras} alt="Tijeras" onClick={() => setEleccionJugador('Tijeras')}/>
            <img className="iconos" src={Lagarto} alt="Lagarto" onClick={() => setEleccionJugador('Lagarto')}/>
            <img className="iconos" src={Spock} alt="Spock" onClick={() => setEleccionJugador('Spock')}/>
            </div>
            <p>Ganadas: {ganadasJugador}</p>
            <span>icono seleccionado de player: {eleccionJugador}</span>
        </div>
    )
}

export default Player;
