import React from 'react'
import '../styles/OpcionesParaJugar.css'
import {useState} from 'react'

import Piedra from '../icons/piedra.svg'
import Papel from '../icons/papel.svg'
import Tijeras from '../icons/tijeras.svg'
import Lagarto from '../icons/lagarto.svg'
import Spock from '../icons/spock.svg'

function OpcionesParaJugar(){

    const [iconoSeleccionado, setSeleccionado] = useState({
        seleccion : ''
    })

    const   handleChange = e => {
        setSeleccionado({
            ...iconoSeleccionado,
            [e.target.iconoSeleccionado] : e.target.value
        });
        console.log(iconoSeleccionado)
    }

    return(
        <div className="iconos-menu">
            <img className="iconos" src={Piedra} alt="Piedra" onClick={handleChange("Piedra")}/>
            <img className="iconos" src={Papel} alt="Papel" onClick={handleChange("Papel")}/>
            <img className="iconos" src={Tijeras} alt="Tijeras" onClick={handleChange("Tijeras")}/>
            <img className="iconos" src={Lagarto} alt="Lagarto" onClick={handleChange("Lagarto")}/>
            <img className="iconos" src={Spock} alt="Spock" onClick={handleChange("Spock")}/>
        </div>
    )
}

export default OpcionesParaJugar;
