import React, { useState } from 'react'

import Player from '../components/Player'


function SinglePlayer(){

    //Jugador
    const [eleccionJugador, setEleccionjugador]= useState('');
    const [ganadasJugador, setGanadasJugador]= useState(0);

    //CPU
    const eleccionesPosibles = ['Piedra', 'Papel', 'Tijeras', 'Lagarto', 'Spock'];
    const eleccionCPU = eleccionesPosibles[Math.floor(Math.random() * eleccionesPosibles.length)]
    const [ganadasCPU, setGanadasCPU]= useState(0);



    function jugar(opcJug1 , opcCPU){
        if(opcJug1 ==='Piedra' && opcCPU=== 'Papel'){
            setGanadasCPU(ganadasCPU + 1)
        }
    }

    return(
        <div>
            <Player/>
            {/* <Player/> */}
            <button className="boton-jugar" type="submit" onClick={jugar(eleccionJugador,eleccionCPU)}>Jugar</button>
            <button className="boton-jugar" type="submit">Jugar de Nuevo</button>
            <h1>El CPU eligi√≥ : {eleccionCPU}</h1>
            <h3>Ganadas CPU: {ganadasCPU}</h3>
        </div>
    )
}

export default SinglePlayer;
