import React from 'react';
import '../styles/Menu.css'
import {Link} from 'react-router-dom'
import Cerrar from '../icons/cancelar.svg'


function Menu(){

    mostrarReglas();{
        document.getElementById("mostrarReglas").style.display="block";
    }

    ocultarReglas();{
        document.getElementById("mostrarReglas").style.display="none";
    }

    return(
        <div className="body-menu">
            <div className="cuadro-botones">
                <Link to="/single-player">
                    <button className="boton" type="button">1 Jugador</button> 
                </Link>
                <button className="boton" type="button">2 Jugadores</button>
                <button className="boton" type="button">Reglas</button>

                <div id="mostrarReglas">
                    <img src={Reglas} alt="reglas del juego"/>
                    <img src={Cerrar} alt="boton cerrar" onClick={mostrarReglas()} />
                </div>
            </div>

        </div>
    )

}


export default Menu;
