import React from 'react'
import '../styles/OpcionesParaJugar.css'
import {useState} from 'react'

import Piedra from '../icons/piedra.svg'
import Papel from '../icons/papel.svg'
import Tijeras from '../icons/tijeras.svg'
import Lagarto from '../icons/lagarto.svg'
import Spock from '../icons/spock.svg'

function OpcionesParaJugar({consultaEleccion}){

    const [iconoSeleccionado, setSeleccionado] = useState('')


    function consultarEleccion(eleccion){
        //e.preventDefault();
        setSeleccionado(eleccion)
        //pasa al componente que utiliza este componente el icono
        consultaEleccion(iconoSeleccionado);
    }

    return(
        <div className="iconos-menu">
            <img className="iconos" src={Piedra} alt="Piedra" onClick={() => consultarEleccion('Piedra')}/>
            <img className="iconos" src={Papel} alt="Papel" onClick={() => setSeleccionado('Papel')}/>
            <img className="iconos" src={Tijeras} alt="Tijeras" onClick={() => setSeleccionado('Tijeras')}/>
            <img className="iconos" src={Lagarto} alt="Lagarto" onClick={() => setSeleccionado('Lagarto')}/>
            <img className="iconos" src={Spock} alt="Spock" onClick={() => setSeleccionado('Spock')}/>
            <span>icono seleccionado: {iconoSeleccionado}</span>
        </div>
    )
}

export default OpcionesParaJugar;
